$(function () {
	var arr = Array('commentSec_1', 'commentSec_2', 'commentSec_3', 'commentSec_4');
	max = 0;
	for (i = 0; i < arr.length; i++) {
		if (max < $('#' + arr[i]).height()) {
			max = $('#' + arr[i]).height();
		}
	}
	for (i = 0; i < arr.length; i++) {
		$('#' + arr[i]).css('height', max);
	}
});
$(document).ready(function () {
	$("#lang_switcher").bind('mouseenter click', function () {
		$("#lang_switcher dd ul").show();
		$('#lang_switcher dt').addClass('active');
	}).bind('mouseleave', function () {
		$("#lang_switcher dd ul").hide();
		$('#lang_switcher dt').removeClass('active');
	});
});
function logi(loc) {
	$("#loc").val(loc);
	$("#lg-modal-frm").modal({
		overlayCss : {
			backgroundColor : "#000"
		},
		onOpen : function (dialog) {
			dialog.overlay.fadeIn('fast', function () {
				dialog.data.hide();
				dialog.container.fadeIn('fast', function () {
					dialog.data.slideDown('fast');
				});
			});
		}
	});
}



